sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
// 		g_form.clearOptions('u_criterion_2');
// 		g_form.setValue('u_criterion_2', '', '');
		g_form.setDisplay('u_criterion_2', false);
		//g_form.setValue('u_criterion_1','EMPTY');
		return;
	}

	// Instantiate the GetTeacher Script Include
	var funcGetFromParentID = new GlideAjax('ClientCategoryMasterUtils');
	// Specify the getTeacher method
	funcGetFromParentID.addParam('sysparm_name', 'getFromParentID');
	// Pass the Requested for tchr_id
	funcGetFromParentID.addParam('sysparm_parentID', g_form.getValue('u_criterion_1'));
	// Send the request to the server
	funcGetFromParentID.getXML(populateData);

	// When the response is back from the server
	function populateData(response) {
		var answer = JSON.parse(response.responseXML.documentElement.getAttribute("answer"));
		g_form.clearOptions('u_criterion_2');
		answer.unshift({id : '', name: '-- None --'});
		setTimeout(function(){
			//g_form.addOption('u_criterion_2', '', '-- None --');
			answer.forEach(function(item) {
				g_form.addOption('u_criterion_2', item.id, item.name);
			});
			if (!g_form.isNewRecord()) {
				g_form.setValue('u_criterion_2', g_scratchpad.u_criterion_2_old);
			}
		}, 100);
		if (answer.length > 0) {
			g_form.setDisplay('u_criterion_2', true);
		}
	}
}
